mapper.update:
update product_t
set pd_status = 0
where pd_idx = #{pd_idx}

mapper.select:
SELECT *
FROM (
SELECT @RN:=@RN+1 AS rnum, a.*
FROM (
SELECT *
FROM product_t

    pd_status = 1

        AND pd_name LIKE CONCAT('%', #{searchValue}, '%')


        AND ct_idx = #{ct_idx}


ORDER BY pd_idx
) a, (SELECT @RN:=0) b
) c
where c.rnum BETWEEN #{start} AND #{end}

SELECT * FROM product_t
ORDER BY pd_date desc
limit 6;
